import turtle
from turtle import tracer, speed, hideturtle, bgcolor, fillcolor, penup, goto, begin_fill, setheading, forward, right, end_fill


def mona():
    tracer(100, 0)
    speed(0)
    hideturtle()

    mona_lisa_data = ''

    def draw_from_data(image_data, image_width, image_height, left, top, pixel_size, inverted='0'):
        penup()
        in_binary = bin(int(image_data, 16))
        in_binary = in_binary[2:]  # remove '0b' from start

        in_binary = in_binary.rjust(
            image_width * image_height, '0')  # add leading zeros
        # TODO: is this even needed?
        #assert not (len(in_binary) > image_width * image_height), 'Too much image data for the width x height size given.'

        for y in range(image_height):
            for x in range(image_width):
                goto(left + (x * pixel_size), top - (y * pixel_size))

                # (!) Try switching this to '0'.
                if in_binary[y * image_width + x] == inverted:
                    begin_fill()
                    setheading(0)
                    forward(pixel_size)  # draw top of the box
                    right(90)
                    forward(pixel_size)  # draw right edge of the box
                    right(90)
                    forward(pixel_size)  # draw bottom of the box
                    right(90)
                    forward(pixel_size)  # draw left edge of the box
                    end_fill()          # fill in the box

    bgcolor('#FFF7D0')
    fillcolor('red')
    draw_from_data(mona_lisa_data, 68, 100, -272, 400, 4)
    fillcolor('green')
    draw_from_data(mona_lisa_data, 68, 100, 0, 400, 4)
    fillcolor('blue')
    draw_from_data(mona_lisa_data, 68, 100, -272, 0, 4)
    fillcolor('black')
    draw_from_data(mona_lisa_data, 68, 100, 0, 0, 4, '1')

    update()
    exitonclick()
